CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `vw_lista_geral_exames` AS select `e`.`id_exame` AS `id_exame`,`e`.`num_exame` AS `num_exame`,date_format(`e`.`data_exame`,'%d/%m/%Y') AS `dt_exame`,`m`.`nome_med` AS `medico`,`l`.`nome_lab` AS `lab`,`te`.`tipo_exame` AS `tipo_exame` from (((`tb_exame` `e` join `tb_medico` `m` on((`m`.`id_medico` = `e`.`fk_medico`))) join `tb_laboratorio` `l` on((`l`.`id_laboratorio` = `e`.`fk_laboratorio`))) join `tb_tipo_exame` `te` on((`te`.`id_tipo_exame` = `e`.`fk_tipo_exame`))) order by `e`.`data_exame`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `vw_ver_exame_por_id` AS select `e`.`id_exame` AS `id_exame`,`e`.`num_exame` AS `num_exame`,date_format(`e`.`data_exame`,'%d/%m/%Y') AS `dt_exame`,`ge`.`nome_grupo` AS `nome_grupo`,`ne`.`nome_exame` AS `exame`,concat(`re`.`resultado`,' ',`ne`.`medida`) AS `resultado`,`re`.`id_resultado_exame` AS `id_resultado_exame`,`ge`.`id_grupo_exame` AS `id_grupo_exame`,`ne`.`id_nome_exame` AS `id_nome_exame` from (((`tb_exame` `e` join `tb_resultado_exame` `re` on((`e`.`id_exame` = `re`.`fk_exame`))) join `tb_nome_exame` `ne` on((`ne`.`id_nome_exame` = `re`.`fk_nome_exame`))) join `tb_grupo_exame` `ge` on((`ge`.`id_grupo_exame` = `ne`.`fk_grupo_exame`)));
